function countingValleys(steps, path) {
    // Write your code here
    let preAlt = 0;
    let altitude = 0;
    let valleyCount = 0;

    for (let i = 0; i < steps; i++) {

        preAlt = altitude;
        if (path[i] === "D") {
            altitude -= 1;
        } else {
            altitude += 1;
        }        


        if (path[i] === "U" && altitude === 0 && preAlt < 0) {
            valleyCount += 1;
        }
    }

    return valleyCount;
}

